import React, {useState, useEffect} from 'react';
import axios from 'axios'
function InstagramLogin(props) {

    const [Pages, setPages] = useState({})
    const [FbId, setFbId] = useState("")
    const [IgId, setIgId] = useState("")

    let login = () =>{
        axios.get('http://localhost:5000/login/groups?')
        .then(response => {
            setPages(response.data.data)
        })
    }

    const [Content, setContent] = useState(<button onClick={login}>Insta Login</button>)


        if(Pages.length !== undefined){
            console.log(Pages.length)
            var display2 = (<option value=''>Choose a page</option>)
            for (var i = 0; i < Pages.length; i++) {
                    display2+=(<option value={Pages[i].id}>{Pages[i].name}</option>)
                    console.log(display2)
            }
            var display = (<select>{display2}</select>)
            setContent(display) //change login button to page selector

            var el = document
            .getElementById('ig-select')
            if(el) {
            el.addEventListener('input', handleSelect)}
        
        //setting the FbId variable to the id of the selected facebook page
        function handleSelect(event) {
            setFbId(event.target.value)
        }
        setPages({})
    }


    return (
        <div>
            {Content}
        </div>
    );
}

export default InstagramLogin;
